svg.script:
      function updateCountdown() {
        var now = new Date(); 
        var examDate = new Date("2023-06-10"); 
        var timeDiff = examDate.getTime() - now.getTime(); 
        var daysDiff = Math.ceil(timeDiff / (1000 * 3600 * 24));

        var countdown1El = document.getElementById("countdown1");
        if (daysDiff == 0) {
          countdown1El.textContent = "00";
        } else if (daysDiff == 1) {
          countdown1El.textContent = "01";
        } else if (daysDiff == 2) {
          countdown1El.textContent = "02";
        } else if (daysDiff == 3) {
          countdown1El.textContent = "03";
        } else if (daysDiff == 4) {
          countdown1El.textContent = "04";
        } else if (daysDiff == 5) {
          countdown1El.textContent = "05";
        } else if (daysDiff == 6) {
          countdown1El.textContent = "06";
        } else if (daysDiff == 7) {
          countdown1El.textContent = "07";
        } else if (daysDiff == 8) {
          countdown1El.textContent = "08";
        } else if (daysDiff == 9) {
          countdown1El.textContent = "09";
        } else {
          countdown1El.textContent = daysDiff;
        }

        var countdown2El = document.getElementById("countdown2");
        if (daysDiff === 0) {
          countdown2El.textContent = "IN 0 DAY";
        } else if (daysDiff === 1) {
          countdown2El.textContent = "IN 1 DAY";
        } else {
          countdown2El.textContent = "IN " + daysDiff + " DAYS";
        }
      }

      updateCountdown();

	// 文字排版由 PowerPoint 制作完成，JavaScript 部分由 ChatGPT 编写
